package java_wk2;

public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//looping means to continue based on a given condition
		//purpose of this program is to print all the value from 0 to 29
		//debugging means to execute a program based on debugging points. controls the execution of the program
			
			for (int i=0; //starting value i=0
				 i<30; // condition
				 i+=2)
			
			{
				System.out.println(i);
			}

	}

}
